import React, { Component } from 'react';
import './App.css';
import Person from '../src/Person/Person'

class App extends Component {

  state = {
    persons: [
      {name:'Arul', guru:"Muruga"},
      {name:'Selva', guru:"Kandha"},
      {name:'anand', guru:"karthi"},
    ]
  }

  switchNameHandler = () => {
    // console.log('was clicked!');
    this.setState = {
      persons:[
          {name:'Arul Anandhan', guru:'Murugan'},
          {name:'Arul Selva', guru:'Skandhan'},
          {name:'Anand', guru:'Karthikeyan'},
      ]
  }
  }

  render(){
  return (
      <div className='App'>
      <h1> Hello world</h1>
      <button onClick={this.switchNameHandler}>Switch Name</button>
      <Person name={this.state.persons[0].name} guru={this.state.persons[0].guru} />
      <Person name={this.state.persons[1].name} guru={this.state.persons[1].guru}>Telling from my heart</Person>
      <Person name={this.state.persons[2].name} guru={this.state.persons[2].guru} />
      </div> 
  );
}
}

export default App;
