import './App.css';
import Vitamins from '../src/fruits/apple';

class App extends Component{
  state = {
    fruits:[
      {
        name:'apple',
        price:300,
      },
      {
        name:'pome',
        price:170
      },
    ]
  }
render() {
  return(
<div className='Sheer'>
  <h1> best fruits</h1>
  <p>fresh fruits</p>
  <button> choose </button>
  <Vitamins name = {this.state.fruits[0].name}
  price= {this.state.fruits[0].price}></Vitamins>
  <Vitamins name={this.state.fruits[1].name}
  price = {this.state.fruits[1].price}></Vitamins>
  </div>
  )
}
}

export default App;




















